import React, {useState} from 'react'
import {NavLink} from 'react-router-dom'
import styled from 'styled-components'
import { MdClose, MdMenu } from 'react-icons/md';

// style
const NavStyles = styled.nav`
  position: fixed;
  z-index: 100;
  top: 0;
  left: 0;
  width: 100%;
  padding: 1rem 0;
  background: var(--deep-dark);
  ul {
    max-width: 1200px;
    margin: 0 auto;
    width: 90%;
    text-align: center;
    li {
      display: inline-block;
      border-radius: 8px;
      transition: 0.3s ease background-color;
      &:hover {
        background-color: var(--deep-dark);
      }
    }
    a {
      display: inline-block;
      padding: 1rem 2rem;
      font-size: 2rem;
      color: var(--white);
      outline: none;
    }
    .active {
      color: yellow;
    }
  }
  .mobile-menu-icon {
    position: absolute;
    right: 1rem;
    top: 1rem;
    width: 4rem;
    cursor: pointer;
    display: none;
    outline: none;
    * {
      pointer-events: none;
    }
  }
  .navItems .closeNavIcon {
    display: none;
  }
  @media only screen and (max-width: 768px) {
    padding: 0;
    .hide-item {
      transform: translateY(calc(-100% - var(--top)));
    }
    .mobile-menu-icon {
      display: block;
    }
    .navItems {
      --top: 1rem;
      transition: 0.3s ease transform;
      background-color: var(--deep-dark);
      padding: 2rem;
      width: 90%;
      max-width: 300px;
      border-radius: 12px;
      position: absolute;
      right: 1rem;
      top: var(--top);
      .closeNavIcon {
        display: block;
        width: 3rem;
        margin: 0 0 0 auto;
        cursor: pointer;
        * {
          pointer-events: none;
        }
      }
      li {
        display: block;
        margin-bottom: 1rem;
      }
    }
  }
`;


export default function NavBar() {
  const [showNav,setShowNav] = useState(false)
return (
      <NavStyles>
       <div className="mobile-menu-icon" onClick={() => setShowNav(!showNav)}>
        <MdMenu style={{color: 'white'}}/>
      </div>
         <ul className={!showNav ? 'navItems hide-item' : 'navItems'}>
           <div className="closeNavIcon" onClick={() => setShowNav(!showNav)} >
          <MdClose style={{color: 'white'}}/>
        </div>
           <li>
             <NavLink onClick={() => setShowNav(!showNav)} to='/' exact>01. Home</NavLink>
           </li>
            <li>
             <NavLink onClick={() => setShowNav(!showNav)} to='About'>02. About</NavLink>
           </li>
            <li>
             <NavLink onClick={() => setShowNav(!showNav)} to='/Projects'>03. Work</NavLink>
           </li>
            <li>
             <NavLink onClick={() => setShowNav(!showNav)} to='/Contact'>04. Contact</NavLink>
           </li>
         </ul>
      </NavStyles>

  );
}
